import React from 'react';

const StockCard = ({ symbol, stock, onClick }) => {
  const isPositive = stock.change >= 0;

  return (
    <div className="stock-card" onClick={onClick}>
      <div className="stock-symbol">{symbol}</div>
      <div className="stock-price-large">${stock.price.toFixed(2)}</div>
      <div className={`stock-change-simple ${isPositive ? 'positive' : 'negative'}`}>
        {isPositive ? '+' : ''}{stock.change.toFixed(2)} ({isPositive ? '+' : ''}{stock.changePercent.toFixed(2)}%)
      </div>
      <div className="click-hint">Tap for deets ðŸ‘†</div>
    </div>
  );
};

export default StockCard;
