import React, { useState, useEffect } from 'react';
import './App.css';
import StockCard from './components/StockCard';
import AnalysisModal from './components/AnalysisModal';
import SearchCard from './components/SearchCard';
import Dashboard from './components/Dashboard';
import Chatbot from './components/Chatbot';

function App() {
  const [realTimeData, setRealTimeData] = useState({});
  const [selectedStock, setSelectedStock] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [predictionResult, setPredictionResult] = useState(null);
  const [isChatOpen, setIsChatOpen] = useState(false);

  const DEFAULT_STOCKS = ['AAPL', 'GOOGL', 'TSLA', 'MSFT', 'AMZN', 'NVDA'];

  const fetchStockData = async () => {
    try {
      const promises = DEFAULT_STOCKS.map(async (symbol) => {
        try {
          const response = await fetch(`http://localhost:8000/api/quote?symbol=${symbol}`);
          const data = await response.json();
          if (data.error) return null;
          return {
            [symbol]: {
              price: data.price,
              change: data.change,
              changePercent: data.change_percent,
              company: getCompanyName(symbol),
              sector: getSector(symbol),
              marketCap: getMarketCap(symbol), // These remain static/mock for now as API doesn't return them
              volume: '10M', // Placeholder
              pe: 25.0 // Placeholder
            }
          };
        } catch (e) {
          console.error(`Error fetching ${symbol}:`, e);
          return null;
        }
      });

      const results = await Promise.all(promises);
      const newData = results.reduce((acc, curr) => ({ ...acc, ...curr }), {});
      
      setRealTimeData(prev => ({ ...prev, ...newData }));
    } catch (error) {
      console.error('Error fetching stock data:', error);
    }
  };

  // Helper functions for static data
  const getCompanyName = (symbol) => {
    const map = {
      'AAPL': 'Apple Inc.', 'GOOGL': 'Alphabet Inc.', 'TSLA': 'Tesla Inc.',
      'MSFT': 'Microsoft Corp.', 'AMZN': 'Amazon.com Inc.', 'NVDA': 'NVIDIA Corp.'
    };
    return map[symbol] || symbol;
  };

  const getSector = (symbol) => {
    const map = {
      'AAPL': 'Technology', 'GOOGL': 'Technology', 'TSLA': 'Automotive',
      'MSFT': 'Technology', 'AMZN': 'E-commerce', 'NVDA': 'Technology'
    };
    return map[symbol] || 'Technology';
  };

  const getMarketCap = (symbol) => {
    const map = {
      'AAPL': '2.8T', 'GOOGL': '1.9T', 'TSLA': '790B',
      'MSFT': '2.5T', 'AMZN': '1.6T', 'NVDA': '2.1T'
    };
    return map[symbol] || '1T';
  };

  useEffect(() => {
    fetchStockData();
    const interval = setInterval(fetchStockData, 5000); // Poll every 5 seconds
    return () => clearInterval(interval);
  }, []);

  const handleStockClick = (symbol) => {
    setSelectedStock(symbol);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedStock(null);
  };

  const handlePredictStock = async (symbol) => {
    try {
      // 1. Fetch current price
      const quoteResponse = await fetch(`http://localhost:8000/api/quote?symbol=${symbol}`);
      const quoteData = await quoteResponse.json();
      
      if (quoteData.error) {
        alert(`Error: ${quoteData.error}`);
        return;
      }

      // 2. Fetch prediction
      const predictResponse = await fetch(`http://localhost:8000/api/predict?symbol=${symbol}&days=7`);
      const predictData = await predictResponse.json();

      if (predictData.error) {
        alert(`Error: ${predictData.error}`);
        return;
      }

      const currentPrice = quoteData.price;
      const forecast = predictData.forecast;
      const lastPoint = forecast[forecast.length - 1];
      const predictedPrice = parseFloat(lastPoint.yhat).toFixed(2);
      const change = ((predictedPrice - currentPrice) / currentPrice * 100).toFixed(2);
      
      // Mocking confidence based on "yhat_lower" and "yhat_upper" spread loosely, or just random for now as API doesn't give "confidence score"
      const spread = lastPoint.yhat_upper - lastPoint.yhat_lower;
      const confidence = Math.max(0, Math.min(100, 100 - (spread / predictedPrice * 100 * 2))).toFixed(0);

      setPredictionResult({
        symbol,
        company: getCompanyName(symbol), // Using helper or fallback
        currentPrice: currentPrice,
        predictedPrice: parseFloat(predictedPrice),
        change: parseFloat(change),
        confidence: parseInt(confidence)
      });

    } catch (error) {
      console.error("Prediction error:", error);
      alert("Failed to get prediction. Ensure backend is running.");
    }
  };

  return (
    <div className="App">
      <div className="container">
        <header className="header">
          <h1>ðŸš€ MarketVision Pro</h1>
          <p>Next-Gen Stock Analysis & Prediction Platform âœ¨</p>
        </header>

        <section className="featured-stocks">
          <h2 className="section-title">ðŸ”¥ Trending Stocks</h2>
          <div className="stocks-grid">
            {Object.keys(realTimeData).map(symbol => (
              <StockCard
                key={symbol}
                symbol={symbol}
                stock={realTimeData[symbol]}
                onClick={() => handleStockClick(symbol)}
              />
            ))}
          </div>
        </section>

        <section className="analysis-section">
          <SearchCard
            onPredict={handlePredictStock}
            predictionResult={predictionResult}
            realTimeData={realTimeData}
          />
        </section>

        <Dashboard realTimeData={realTimeData} />

        <footer className="footer">
          <button className="chat-trigger-btn" onClick={() => setIsChatOpen(true)}>
            <span style={{ fontSize: '1.5rem' }}>ðŸ¤–</span> 
            Ask AI Assistant
          </button>
          <div style={{ marginTop: '20px', opacity: 0.7, fontSize: '0.9rem' }}>
            Â© 2024 MarketVision Pro. Powered by FinBERT & Prophet.
          </div>
        </footer>
      </div>

      {showModal && selectedStock && (
        <AnalysisModal
          symbol={selectedStock}
          stock={realTimeData[selectedStock]}
          onClose={closeModal}
        />
      )}
      
      <Chatbot isOpen={isChatOpen} onToggle={setIsChatOpen} />
    </div>
  );
}

export default App;
