import React, { useState, useEffect } from 'react';
import './App.css';
import StockCard from './components/StockCard';
import AnalysisModal from './components/AnalysisModal';
import SearchCard from './components/SearchCard';
import Dashboard from './components/Dashboard';
import Chatbot from './components/Chatbot';

function App() {
  const [isChatOpen, setIsChatOpen] = useState(false);

  // ... (rest of code)

  return (
    <div className="App">
      <div className="container">
        {/* ... (existing header and sections) ... */}
        <header className="header">
          <h1>ðŸš€ MarketVision Pro</h1>
          <p>Next-Gen Stock Analysis & Prediction Platform âœ¨</p>
        </header>

        <section className="featured-stocks">
          <h2 className="section-title">ðŸ”¥ Trending Stocks</h2>
          <div className="stocks-grid">
            {Object.keys(realTimeData).map(symbol => (
              <StockCard
                key={symbol}
                symbol={symbol}
                stock={realTimeData[symbol]}
                onClick={() => handleStockClick(symbol)}
              />
            ))}
          </div>
        </section>

        <section className="analysis-section">
          <SearchCard
            onPredict={handlePredictStock}
            predictionResult={predictionResult}
            realTimeData={realTimeData}
          />
        </section>

        <Dashboard realTimeData={realTimeData} />

        <footer className="footer">
          <button className="chat-trigger-btn" onClick={() => setIsChatOpen(true)}>
            <span style={{ fontSize: '1.5rem' }}>ðŸ¤–</span> 
            Ask AI Assistant
          </button>
          <div style={{ marginTop: '20px', opacity: 0.7, fontSize: '0.9rem' }}>
            Â© 2024 MarketVision Pro. Powered by FinBERT & Prophet.
          </div>
        </footer>
      </div>

      {showModal && selectedStock && (
        <AnalysisModal
          symbol={selectedStock}
          stock={realTimeData[selectedStock]}
          onClose={closeModal}
        />
      )}
      
      <Chatbot isOpen={isChatOpen} onToggle={setIsChatOpen} />
    </div>
  );
}

export default App;
