import React, { useState } from 'react';

const SearchCard = ({ onPredict, predictionResult, realTimeData }) => {
  const [searchSymbol, setSearchSymbol] = useState('AAPL');

  const handlePredict = () => {
    onPredict(searchSymbol);
  };

  const getStockName = (symbol) => {
    const names = {
      'AAPL': 'AAPL - Apple Inc.',
      'GOOGL': 'GOOGL - Alphabet Inc.',
      'TSLA': 'TSLA - Tesla Inc.',
      'MSFT': 'MSFT - Microsoft Corp.',
      'AMZN': 'AMZN - Amazon.com Inc.',
      'NVDA': 'NVDA - NVIDIA Corp.'
    };
    return names[symbol] || `${symbol} - Stock Analysis`;
  };

  return (
    <div className="search-card">
      <h3>üîç Search & Analyze</h3>
      <div className="stock-input">
        <input
          type="text"
          value={searchSymbol}
          onChange={(e) => setSearchSymbol(e.target.value.toUpperCase())}
          placeholder="Enter stock symbol (e.g., AAPL)"
        />
        <button className="btn" onClick={handlePredict}>
          Analyze
        </button>
      </div>
      
      {predictionResult && (
        <div className="prediction-result">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <div style={{ fontSize: '1.2rem', fontWeight: 'bold', color: '#2d3748' }}>
                {getStockName(predictionResult.symbol)}
              </div>
              <div style={{ color: '#718096' }}>
                Current: ${predictionResult.currentPrice.toFixed(2)} <span style={{ color: '#38a169' }}>‚óè</span>
              </div>
            </div>
            <div style={{ textAlign: 'right' }}>
              <div style={{ 
                fontSize: '1.5rem', 
                fontWeight: 'bold', 
                color: predictionResult.change >= 0 ? '#38a169' : '#e53e3e' 
              }}>
                ${predictionResult.predictedPrice.toFixed(2)}
              </div>
              <div style={{ color: '#718096' }}>7-day prediction</div>
            </div>
          </div>
          <div style={{ marginTop: '15px' }}>
            <div style={{ fontSize: '0.9rem', color: '#4a5568' }}>
              Confidence: {predictionResult.confidence}% | Expected {predictionResult.change >= 0 ? 'gain' : 'loss'}: {predictionResult.change >= 0 ? '+' : ''}{predictionResult.change.toFixed(2)}%
            </div>
            <div style={{ fontSize: '0.8rem', color: '#718096', marginTop: '5px' }}>
              Last updated: {new Date().toLocaleTimeString()}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchCard;
