import React, { useState, useRef, useEffect } from 'react';

const Chatbot = ({ isOpen, onToggle }) => {
  const [messages, setMessages] = useState([
    { role: 'assistant', content: 'Hello! I am your AI Market Assistant. Ask me about market news or sentiments!' }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const scrollRef = useRef(null);
  
  // Use prop if provided, otherwise could fallback but for this app we'll control it from App
  
  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages, isOpen]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = input;
    setMessages(prev => [...prev, { role: 'user', content: userMessage }]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:8000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: userMessage }),
      });

      const data = await response.json();

      if (data.error) {
        setMessages(prev => [...prev, { role: 'assistant', content: `Error: ${data.error}` }]);
      } else {
        const sentiment = data.sentiment;
        const sentimentDetails = `
          \n**FinBERT**: ${sentiment['FinBERT Label']} (${sentiment['FinBERT Confidence']})
          \n**VADER**: ${sentiment['VADER Compound']}
        `;
        
        setMessages(prev => [...prev, { 
          role: 'assistant', 
          content: data.response, 
          details: sentimentDetails 
        }]);
      }
    } catch (error) {
      setMessages(prev => [...prev, { role: 'assistant', content: 'Failed to reach the brain. Is the backend running?' }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={{ position: 'fixed', bottom: '20px', right: '20px', zIndex: 1000 }}>
      {isOpen ? (
        <div style={{ 
          width: '350px', 
          height: '500px', 
          backgroundColor: '#1a202c', 
          borderRadius: '12px', 
          boxShadow: '0 4px 20px rgba(0,0,0,0.5)',
          display: 'flex',
          flexDirection: 'column',
          border: '1px solid rgba(255,255,255,0.1)'
        }}>
          <div style={{ 
            padding: '15px', 
            borderBottom: '1px solid rgba(255,255,255,0.1)',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            backgroundColor: '#2d3748',
            borderTopLeftRadius: '12px',
            borderTopRightRadius: '12px'
          }}>
            <h3 style={{ margin: 0, color: 'white' }}>ðŸ¤– Market Bot</h3>
            <button 
              onClick={() => onToggle(false)}
              style={{ background: 'none', border: 'none', color: 'white', cursor: 'pointer', fontSize: '1.2rem' }}
            >
              &times;
            </button>
          </div>

          <div ref={scrollRef} style={{ flex: 1, overflowY: 'auto', padding: '15px', display: 'flex', flexDirection: 'column', gap: '10px' }}>
            {messages.map((msg, idx) => (
              <div key={idx} style={{ 
                alignSelf: msg.role === 'user' ? 'flex-end' : 'flex-start',
                backgroundColor: msg.role === 'user' ? '#4299e1' : '#2d3748',
                color: 'white',
                padding: '10px 15px',
                borderRadius: '10px',
                maxWidth: '80%',
                fontSize: '0.9rem'
              }}>
                <div>{msg.content}</div>
                {msg.details && (
                  <div style={{ marginTop: '5px', fontSize: '0.8rem', color: '#cbd5e0', whiteSpace: 'pre-wrap' }}>
                    {msg.details}
                  </div>
                )}
              </div>
            ))}
            {isLoading && (
              <div style={{ alignSelf: 'flex-start', color: '#a0aec0', fontSize: '0.8rem', marginLeft: '10px' }}>
                Thinking...
              </div>
            )}
          </div>

          <form onSubmit={handleSubmit} style={{ padding: '15px', borderTop: '1px solid rgba(255,255,255,0.1)' }}>
            <div style={{ display: 'flex', gap: '10px' }}>
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Ask about market news..."
                style={{
                  flex: 1,
                  padding: '10px',
                  borderRadius: '6px',
                  border: '1px solid #4a5568',
                  backgroundColor: '#2d3748',
                  color: 'white',
                  outline: 'none'
                }}
              />
              <button 
                type="submit"
                disabled={isLoading}
                style={{
                  padding: '10px 15px',
                  borderRadius: '6px',
                  border: 'none',
                  backgroundColor: '#48bb78',
                  color: 'white',
                  cursor: isLoading ? 'not-allowed' : 'pointer',
                  fontWeight: 'bold'
                }}
              >
                Send
              </button>
            </div>
          </form>
        </div>
      ) : (
        <button 
          onClick={() => onToggle(true)}
          style={{
            width: '60px',
            height: '60px',
            borderRadius: '50%',
            backgroundColor: '#4299e1',
            color: 'white',
            border: 'none',
            boxShadow: '0 4px 10px rgba(0,0,0,0.3)',
            cursor: 'pointer',
            fontSize: '1.5rem',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}
        >
          ðŸ’¬
        </button>
      )}
    </div>
  );
};

export default Chatbot;
