import React, { useEffect, useRef } from 'react';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

const AnalysisModal = ({ symbol, stock, onClose }) => {
  const modalRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        onClose();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [onClose]);

  useEffect(() => {
    document.body.style.overflow = 'hidden';
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, []);

  const isPositive = stock.change >= 0;
  // Predictions in modal are also mocked/static for now unless we fetch them too. Keeping them static or linked to the main prediction if passed.
  // For now, let's keep the extra analysis items as static/calculated since the backend doesn't provide RSI/MACD yet.
  
  const predictedPrice = (stock.price * (1 + (Math.random() * 0.2 - 0.1))).toFixed(2); // Still mock for modal quick view
  const confidence = (Math.random() * 30 + 60).toFixed(0);
  const rsi = Math.floor(Math.random() * 100);
  const macd = (Math.random() * 10 - 5).toFixed(1);

  const [chartData, setChartData] = React.useState({
    labels: [],
    datasets: []
  });

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/daily?symbol=${symbol}&outputsize=compact`);
        const data = await response.json();
        
        if (data.error) return;

        setChartData({
          labels: data.labels,
          datasets: [{
            label: `${symbol} Price`,
            data: data.closes,
            borderColor: '#667eea',
            backgroundColor: 'rgba(102, 126, 234, 0.1)',
            tension: 0.4,
            fill: true,
            pointRadius: 2, // Slightly smaller for real data density
            pointHoverRadius: 6,
            pointBackgroundColor: '#667eea',
            pointBorderColor: '#fff',
            pointBorderWidth: 2
          }]
        });
      } catch (err) {
        console.error("Failed to fetch history:", err);
      }
    };

    fetchHistory();
  }, [symbol]);

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false
      }
    },
    scales: {
      y: {
        beginAtZero: false,
        grid: {
          color: 'rgba(0,0,0,0.1)'
        }
      },
      x: {
        grid: {
          color: 'rgba(0,0,0,0.1)'
        }
      }
    }
  };

  return (
    <div className="analysis-modal active">
      <div className="modal-content" ref={modalRef}>
        <div className="modal-header">
          <h2 className="modal-title">{symbol} - {stock.company}</h2>
          <button className="close-btn" onClick={onClose}>&times;</button>
        </div>
        
        <div className="modal-chart">
          <Line data={chartData} options={chartOptions} />
        </div>
        
        <div className="modal-analysis">
          <div className="analysis-item">
            <h4>Current Price</h4>
            <div className="value">${stock.price.toFixed(2)}</div>
            <div className={`change ${isPositive ? 'positive' : 'negative'}`}>
              {isPositive ? '+' : ''}{stock.change.toFixed(2)} ({isPositive ? '+' : ''}{stock.changePercent.toFixed(2)}%)
            </div>
          </div>
          
          <div className="analysis-item">
            <h4>Predicted Price</h4>
            <div className="value">${predictedPrice}</div>
            <div className={`change ${predictedPrice > stock.price ? 'positive' : 'negative'}`}>
              {predictedPrice > stock.price ? '+' : ''}{((predictedPrice - stock.price) / stock.price * 100).toFixed(2)}%
            </div>
          </div>
          
          <div className="analysis-item">
            <h4>Confidence</h4>
            <div className="value">{confidence}%</div>
            <div className="change positive">High</div>
          </div>
          
          <div className="analysis-item">
            <h4>RSI</h4>
            <div className="value">{rsi}</div>
            <div className={`change ${rsi > 70 ? 'negative' : rsi < 30 ? 'positive' : 'neutral'}`}>
              {rsi > 70 ? 'Overbought' : rsi < 30 ? 'Oversold' : 'Neutral'}
            </div>
          </div>
          
          <div className="analysis-item">
            <h4>MACD</h4>
            <div className="value">{macd >= 0 ? '+' : ''}{macd}</div>
            <div className={`change ${macd > 0 ? 'positive' : 'negative'}`}>
              {macd > 0 ? 'Bullish' : 'Bearish'}
            </div>
          </div>
          
          <div className="analysis-item">
            <h4>Market Cap</h4>
            <div className="value">${stock.marketCap}</div>
            <div className="change neutral">{stock.sector}</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AnalysisModal;
