          </div>
        </section>

        <section className="analysis-section">
          <SearchCard
            onPredict={handlePredictStock}
            predictionResult={predictionResult}
            realTimeData={realTimeData}
          />
        </section>

        <Dashboard realTimeData={realTimeData} />

        <footer className="footer">
          <button className="chat-trigger-btn" onClick={() => setIsChatOpen(true)}>
            <span style={{ fontSize: '1.5rem' }}>ðŸ¤–</span> 
            Ask AI Assistant
          </button>
          <div style={{ marginTop: '20px', opacity: 0.7, fontSize: '0.9rem' }}>
            Â© 2024 MarketVision Pro. Powered by FinBERT & Prophet.
          </div>
        </footer>
      </div>

      {showModal && selectedStock && (
        <AnalysisModal
          symbol={selectedStock}
          stock={realTimeData[selectedStock]}
          onClose={closeModal}
        />
      )}
      
      <Chatbot isOpen={isChatOpen} onToggle={setIsChatOpen} />
    </div>
  );
}

export default App;
