import React, { useState, useEffect } from 'react';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

const Dashboard = ({ realTimeData }) => {
  const [marketIndices, setMarketIndices] = useState({
    'SP500': { value: 4200.50, change: 1.2 },
    'NASDAQ': { value: 13150.25, change: -0.8 },
    'DOW': { value: 33850.75, change: 0.5 },
    'VIX': { value: 21.5, change: -2.1 }
  });

  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [{
      label: 'S&P 500',
      data: [],
      borderColor: '#667eea',
      backgroundColor: 'rgba(102, 126, 234, 0.1)',
      tension: 0.4,
      fill: true,
      pointRadius: 0,
      pointHoverRadius: 5
    }]
  });

  useEffect(() => {
    // Generate initial chart data
    const labels = [];
    const data = [];
    
    for (let i = 0; i < 20; i++) {
      const time = new Date();
      time.setMinutes(time.getMinutes() - (20 - i) * 5);
      labels.push(time.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }));
      data.push(4200 + Math.random() * 50 - 25);
    }
    
    setChartData({
      labels,
      datasets: [{
        label: 'S&P 500',
        data,
        borderColor: '#667eea',
        backgroundColor: 'rgba(102, 126, 234, 0.1)',
        tension: 0.4,
        fill: true,
        pointRadius: 0,
        pointHoverRadius: 5
      }]
    });
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setMarketIndices(prev => {
        const newIndices = { ...prev };
        Object.keys(newIndices).forEach(index => {
          const idx = newIndices[index];
          const change = (Math.random() - 0.5) * 0.5;
          idx.change += change;
          idx.value += (idx.value * change / 100);
        });
        return newIndices;
      });

      // Update chart
      setChartData(prev => {
        const newData = [...prev.datasets[0].data];
        const newLabels = [...prev.labels];
        const lastValue = newData[newData.length - 1];
        const newValue = lastValue + (Math.random() * 10 - 5);
        
        newData.shift();
        newData.push(newValue);
        
        newLabels.shift();
        const now = new Date();
        newLabels.push(now.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }));
        
        return {
          ...prev,
          labels: newLabels,
          datasets: [{
            ...prev.datasets[0],
            data: newData
          }]
        };
      });
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    animation: {
      duration: 750,
      easing: 'easeInOutQuart'
    },
    plugins: {
      legend: {
        display: false
      }
    },
    scales: {
      y: {
        beginAtZero: false,
        grid: {
          color: 'rgba(0,0,0,0.1)'
        }
      },
      x: {
        grid: {
          color: 'rgba(0,0,0,0.1)'
        }
      }
    }
  };

  return (
    <div className="dashboard">
      {/* Market Overview Card */}
      <div className="card">
        <h3>ðŸ“Š Market Vibes</h3>
        <div className="market-stats">
          {Object.keys(marketIndices).map((index, idx) => {
            const data = marketIndices[index];
            const change = data.change.toFixed(1);
            const isPositive = change >= 0;
            
            return (
              <div key={index} className="stat-item">
                <div className="stat-value" style={{ color: isPositive ? '#38a169' : '#e53e3e' }}>
                  {isPositive ? '+' : ''}{change}%
                </div>
                <div className="stat-label">{index}</div>
              </div>
            );
          })}
        </div>
        <div className="chart-container">
          <Line data={chartData} options={chartOptions} />
        </div>
      </div>

      {/* Market News Card */}
      <div className="card">
        <h3>ðŸ“° Market Tea â˜•</h3>
        <div className="news-item">
          <div className="news-title">Fed Signals Potential Rate Cut</div>
          <div style={{ fontSize: '0.9rem', color: '#718096' }}>
            Federal Reserve hints at monetary policy changes affecting tech stocks...
          </div>
          <span className="news-impact impact-positive">Positive Impact</span>
        </div>
        <div className="news-item">
          <div className="news-title">Tech Earnings Season Begins</div>
          <div style={{ fontSize: '0.9rem', color: '#718096' }}>
            Major tech companies report Q4 earnings with mixed results...
          </div>
          <span className="news-impact impact-neutral">Neutral Impact</span>
        </div>
        <div className="news-item">
          <div className="news-title">Oil Prices Surge on Supply Concerns</div>
          <div style={{ fontSize: '0.9rem', color: '#718096' }}>
            Energy sector sees significant gains amid geopolitical tensions...
          </div>
          <span className="news-impact impact-negative">Negative Impact</span>
        </div>
      </div>

      {/* Portfolio Tracker Card */}
      <div className="card">
        <h3>ðŸ’Ž Portfolio Flex</h3>
        <div className="portfolio-list">
          {Object.keys(realTimeData).slice(0, 3).map(symbol => {
            const stock = realTimeData[symbol];
            const gain = (Math.random() * 200 - 100).toFixed(2);
            const shares = Math.floor(Math.random() * 20 + 1);
            const isPositive = parseFloat(gain) >= 0;
            
            return (
              <div key={symbol} className="portfolio-item">
                <div>
                  <div style={{ fontWeight: 'bold' }}>{symbol}</div>
                  <div style={{ fontSize: '0.9rem', color: '#718096' }}>{shares} shares</div>
                </div>
                <div className={`portfolio-gain ${isPositive ? 'gain-positive' : 'gain-negative'}`}>
                  {isPositive ? '+' : ''}${gain}
                </div>
              </div>
            );
          })}
        </div>
        <div style={{ marginTop: '20px', paddingTop: '15px', borderTop: '2px solid #e2e8f0' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <span style={{ fontWeight: 'bold' }}>Total Portfolio Value:</span>
            <span style={{ fontSize: '1.3rem', fontWeight: 'bold', color: '#38a169' }}>+$170.95</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
